var $ = require('jquery');

function createTable(board, style, interact) {
  var $table = $('<table>');
  var size = board.length; // we assume rectangular board
  for (var y = 0; y < size; y++) {
    var $row = $('<tr>');
    for (var x = 0; x < size; x++) {
      var $cell = $('<td>');
      $cell.css({
        width: 50,
        height: 50,
        border: '1px solid #000'
      });
      style($cell, board[x][y]);
      $cell.click({ posX: x, posY: y}, interact);
      $row.append($cell);
    }
    $table.append($row);
  }
  return $table;
}

module.exports = createTable;
