var board = [];

module.exports = {
  initialize: function () {
    var size = 8;
    for (var x = 0; x < size; x++) {
      board[x] = [];
      for (var y = 0; y < size; y++) {
        board[x][y] = {};
      }
    }

    board[3][4] = { player: 1 };
    board[2][4] = { player: 2 };
    board[3][1] = { player: 1 };
  },
  placePlayer: function (x, y) {
    console.log('place player', x, y);
    board[x][y] = { player: 1 };
  },
  getBoard: function () {
    return board;
  }
};
