var $ = require('jquery');
var gameState = require('app/gameState');
var stylePlayer = require('app/stylePlayer');
var handleUserClick = require('app/handleUserClick');
var createTable = require('app/createTable');

module.exports = function () {
  $(document).on('ready click', function () {
    var board = gameState.getBoard();
    var $table = createTable(board, stylePlayer, handleUserClick);
    $('body').empty().append($table);
  });
};
